mapper.select:
SELECT COUNT(*) as cnt FROM goods
WHERE itemno=#{itemno}
WHERE itemno=#{itemno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT goodsno, adminno, itemno, seqno, gname, content, word, rdate, file1, file1savedm thumb1, size1, price, dc, saleprice, salecnt, point, r FROM ( SELECT goodsno, adminno, itemno, seqno, gname, content, word, rdate, file1, file1savedm thumb1, size1, price, dc, saleprice, salecnt, point, rownum as r FROM ( SELECT goodsno, adminno, itemno, seqno, gname, content, word, rdate, file1, file1savedm thumb1, size1, price, dc, saleprice, salecnt, point FROM goods
WHERE itemno=#{itemno}
WHERE itemno=#{itemno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY goodsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

mapper.update:
UPDATE goods
SET gname=#{gname}, content=#{content},  word=#{word}
WHERE goodsno = #{goodsno}

