mapper.select:
SELECT COUNT(*) as cnt FROM goods
WHERE itemno=#{itemno}
WHERE itemno=#{itemno} AND (UPPER(gname) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT goodsno, adminno, itemno, gname, content, cnt, word, rdate, file1, file1saved, thumb1, size1, price, dc, saleprice, salecnt, point, r FROM ( SELECT goodsno, adminno, itemno, gname, content, cnt, word, rdate, file1, file1saved, thumb1, size1, price, dc, saleprice, salecnt, point, rownum as r FROM ( SELECT goodsno, adminno, itemno, gname, content, cnt, word, rdate, file1, file1saved, thumb1, size1, price, dc, saleprice, salecnt, point FROM goods
WHERE itemno=#{itemno}
WHERE itemno=#{itemno} AND (UPPER(gname) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY goodsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*)  as cnt 
FROM contents
WHERE goodsno = #{goodsno} AND passwd = #{passwd}

mapper.update:
UPDATE goods
SET gname=#{gname}, content=#{content}, cnt=#{cnt},  word=#{word}, 
        price=#{price}, dc=#{dc}, saleprice=#{saleprice}, origin= #{origin}, exdate= #{exdate}, storage= #{storage}, grams= #{grams}
WHERE goodsno = #{goodsno}

UPDATE goods
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE goodsno = #{goodsno}

mapper.select:
SELECT COUNT(*) as cnt 
