mapper.select:
SELECT COUNT(*) as cnt
FROM notice
WHERE noticeno = #{noticeno} AND passwd = #{passwd}

mapper.update:
UPDATE notice
SET title=#{title}, content=#{content}, word=#{word}
WHERE noticeno = #{noticeno}

UPDATE notice
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE noticeno = #{noticeno}

update notice
set cnt = cnt + 1
where noticeno = #{noticeno}
