mapper.update:
UPDATE recipe 
SET youtube=#{youtube} 
WHERE recipeno=#{recipeno}

mapper.select:
SELECT COUNT(*) as cnt FROM recipe
WHERE itemno=#{itemno}
WHERE itemno=#{itemno} AND (UPPER(title) LIKE '%' || #{word} || '%' OR UPPER(article) LIKE '%' || #{word} || '%' OR UPPER(word) LIKE '%' || #{word} || '%')

SELECT recipeno, memberno, itemno, title, article, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, youtube, mname, gname, time, difficulty, r FROM ( SELECT recipeno, memberno, itemno, title, article, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, youtube, mname, gname, time, difficulty, rownum as r FROM ( SELECT recipeno, memberno, itemno, title, article, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, youtube, mname, gname, time, difficulty FROM recipe
WHERE itemno=#{itemno}
WHERE itemno=#{itemno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(article) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY recipeno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt
FROM recipe
WHERE recipeno = #{recipeno} AND passwd = #{passwd}

mapper.update:
UPDATE recipe
SET title=#{title}, article=#{article}, gname=#{gname}, ingredient=#{ingredient}, word=#{word},
file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE recipeno = #{recipeno}

mapper.select:
select COUNT(*) as cnt
from recipe
where itemno = #{itemno}

mapper.update:
UPDATE recipe
SET cnt = cnt + 1
WHERE recipeno = #{recipeno}

UPDATE recipe 
SET recom = recom + 1 
WHERE recipeno=#{recipeno}

UPDATE recipe 
 SET recom = recom - 1 
 WHERE recipeno=#{recipeno}
