mapper.update:
UPDATE reply
SET ratingAvg = (
    SELECT ROUND(AVG(ratingValue), 2)
    FROM reply
    WHERE recipeno = #{recipeno}
    )
WHERE recipeno = #{recipeno}

mapper.select:
SELECT AVG(ratingValue)
FROM reply 
WHERE recipeno = #{recipeno}

SELECT COUNT(*) as cnt
FROM reply
WHERE recipeno=#{recipieno}

SELECT replyno,memberno,mid,recipeno,replycont,rdate,ratingValue,ratingAVG, r
   FROM (
              SELECT replyno,memberno,mid,recipeno,replycont,rdate,ratingValue,ratingAVG, rownum as r
              FROM (
                         SELECT r.replyno, r.memberno, m.id AS mid, r.recipeno, r.replycont, r.rdate, r.ratingValue,r.ratingAVG
                        FROM reply r 
                        JOIN member m 
                        ON r.memberno = m.memberno
                        WHERE r.recipeno = #{recipeno}
                        ORDER BY rdate DESC
               )
    )
    WHERE  r >= #{start_num} AND r <= #{end_num}

SELECT r.replycont,r.memberno,m.id AS mid,r.replyno
FROM reply r JOIN member m ON r.memberno = m.memberno
WHERE replyno=#{replyno}

mapper.update:
UPDATE reply
SET replycont = #{replycont}
WHERE replyno = #{replyno}
